(()=>{"use strict";const qs=(s,c=document)=>c.querySelector(s),qsa=(s,c=document)=>[...c.querySelectorAll(s)];document.addEventListener("DOMContentLoaded",()=>{prefetchImages(),initCountdown(),initCTA();});function prefetchImages(){const imgs=["/images/logo-bantubet.png","/images/isj-angola.png","/images/qrcode.png"];imgs.forEach(src=>{const i=new Image();i.src=src;i.decoding="async";});}function initCountdown(){const h=qs(".hero");if(!h)return;const d=document.createElement("div");d.id="countdown";d.style.fontWeight="bold";d.style.marginTop="20px";h.appendChild(d);let s=3600;const u=()=>{const m=Math.floor(s/60),sec=s%60;d.textContent=`Oferta expira em ${m}:${sec<10?"0":""}${sec}`;if(s>0){s--}else{d.textContent="Oferta encerrada!";clearInterval(t)}};u();const t=setInterval(u,1000);}
function initCTA(){const url="https://sshortly1.com/registo.bantubet.co.ao";qsa(".btn").forEach(b=>b.addEventListener("click",e=>{e.preventDefault();showToast("🎯 Redirecionando...");setTimeout(()=>window.open(url,"_blank","noopener,noreferrer"),500);}));}function showToast(m){let t=qs("#toast-msg");if(!t){t=document.createElement("div");t.id="toast-msg";t.setAttribute("role","alert");Object.assign(t.style,{position:"fixed",bottom:"20px",left:"50%",transform:"translateX(-50%)",background:"#004080",color:"#fff",padding:"10px 20px",borderRadius:"6px",fontFamily:"Inter,sans-serif",fontSize:"0.9rem",zIndex:9999,opacity:0,transition:"opacity .3s"});document.body.appendChild(t);}t.textContent=m;t.style.opacity="1";setTimeout(()=>t.style.opacity="0",2500);}})();
